<template>
  <div>
    <wux-fab-button
      @iclick="onClick($event)"
      defaultVisible="true"
      position="bottomRight"
      direction="vertical"
      theme="assertive"
      :reverse="reverse"
      spaceBetween="10"
      sAngle="0"
      eAngle="360"
      :buttons="buttons"
    />
    <wux-dialog id="wux-dialog"/>
  </div>
</template>
<script>
import { $wuxToast, $wuxDialog } from "../../static/wux/index";
const homeIcon = 'data:image/png;base64,'
const cartIcon = 'data:image/png;base64,'
const orderIcon = 'data:image/png;base64,'

const buttons = [{
  className: 'fubtn_one',
  icon: orderIcon
},
{
  className: 'fubtn_two',
  icon: cartIcon
},
  // {
  //   className: 'fubtn_three',
  //   icon: homeIcon
  // }
]
export default {
  data () {
    return {
      buttons,
    }
  },
  methods: {
    setData (data) {
      Object.keys(data).forEach(key => {
        this[key] = data[key]
      })
    },
    onClick (e) {
      if (!wx.getStorageSync('userinfo').openId) {
        $wuxDialog().confirm({
          resetOnClose: true,
          closable: true,
          title: "您还没有登陆",
          content: "是否跳转到登陆页面",
          onConfirm (e) {
            wx.switchTab({
              url: "/pages/me/main"
            });
          },
          onCancel (e) {
            return false;
          }
        });
      } else {
        if (e.mp.detail.index === 1) {
          wx.navigateTo({
            url: '/pages/cart/main'
          })
        }
        if (e.mp.detail.index === 0) {
          wx.navigateTo({
            url: '/pages/myorder/main'
          })
        }
      }


    },
  },
}
</script>

